import { mapState } from "vuex";
import Signup from "./Signup";
export default {
  components: {
    Signup
  },
  data() {
    return {
      showpass: false,
      requiredRule: [v => !!v || "Input required"],
      lazy: false,
      valid: true,
      usernameL: "",
      dialog: false,
      username: "",
      pass1: "",
      loading: null,
      show: null,
      message: ""
    };
  },
  methods: {
    clear() {
      this.username = "";
      this.pass1 = "";
    },
    snackBar(text, color) {
      this.$store.commit("SET_SNACKBAR", {
        showing: true,
        text: text,
        color: color
      });
    },
    async cancel() {
      // let res =
       await this.$store.dispatch("callAPI", {
        send: {},
        branch: "/logout/",
        call: "DO_NOTHING",
        setData: {}
      });

    },
    async logout() {
      let res = await this.$store.dispatch("callAPI", {
        send: {},
        branch: "/logout/",
        call: "DO_NOTHING",
        setData: {}
      });
      console.log(res);
      if (res.code == "ok") {
        this.$store.commit("LOG_OUT", false);
        this.$store.commit("SET_SNACKBAR", {
          showing: true,
          text: "Log out successfully",
          color: "success"
        });

        setTimeout(this.$router.push("/"), 1000);
      } else {
        this.$store.commit("SET_SNACKBAR", {
          showing: true,
          text: "Error when logging out",
          color: "error"
        });
      }
    },
    usernameset() {
      this.usernameL = window.localStorage.getItem("username");
    },
    login() {
      if (this.$refs.form1.validate()) {
        this.submit();
      }
    },
    loginAdmin() {
      if (this.$refs.form1.validate()) {
        this.admin();
      }
    },
    async admin() {
      this.loading = true;
      let send = new FormData();
      send.append("username", this.username);
      send.append("password", this.pass1);
      let branch = "/login/";
      let call = "DO_NOTHING";
      let dataSend = {
        send: send,
        branch: branch,
        call: call,
        setData: {}
      };
      try {
        let res = await this.$store.dispatch("callAPI", dataSend);
        console.log(res);
        if (res.code == "ok") {
          if (res.is_staff != "true") {
            this.cancel();
            this.snackBar("Not Admin", "error");
          } else {
            this.$store.commit("SET_ADMIN_LOGIN", this.username);
            this.usernameset();

            this.snackBar("Login successfully", "success");
            this.$router.push("/admin/qr");
          }
        } else {
          this.snackBar("Login failed, Invalid login info", "error");
        }
      } catch {
        this.$store.dispatch("SET_SNACKBAR", {
          showing: true,
          text: "Login error",
          color: "error"
        });
      }
      this.loading = false;
      this.close();
    },
    async submit() {
      this.loading = true;
      let send = new FormData();
      send.append("username", this.username);
      send.append("password", this.pass1);
      let branch = "/login/";
      let call = "DO_NOTHING";
      let dataSend = {
        send: send,
        branch: branch,
        call: call,
        setData: {}
      };
      try {
        let res = await this.$store.dispatch("callAPI", dataSend);
        console.log(res);

        if (res.code == "ok") {
          if (res.is_staff == "true") {
            this.snackBar("Not user", "error");
            this.cancel();
          } else {
            this.$store.commit("SET_USER_LOGIN", this.username);
            this.usernameset();

            this.snackBar("Login successfully", "success");
            setTimeout(this.$router.push("/user/suggest"), 3000);
          }
        } else {
          this.snackBar("Login failed, Invalid login info", "error");
        }
      } catch {
          this.snackBar("Login error", "error");
      }
      this.loading = false;
      this.close();
    },
    sync() {
      this.dialog = this.dialog_login;
    },
    close() {
      this.$store.state.dialog_login = false;
      this.sync();
    },
    open() {
      this.$store.state.dialog_login = true;
      this.sync();
    }
  },
  computed: {
    ...mapState(["isLogin"])
  },
  mounted() {
    this.usernameset();
    this.sync();
  }
  // created() {
  //   this.$store.watch(
  //     state => state.isLogin,
  //     () => {
  //       this.isLogin = this.$store.state.isLogin;
  //     }
  //   );
  // }
};
